config.script:
import com.workfusion.odf2.core.Odf
import com.example.task.EmailBotTask
import com.workfusion.odf2.service.ControlTowerServicesModule
// after imports

// before odf builder
def odf = Odf.builder()
    .withInput(__taskInputMap.getWrappedObject())
    .withBinding(binding)
    .withModules(ControlTowerServicesModule)
    // after modules
    .build()

// after odf builder

result = odf.runTask(EmailBotTask)

// after runTask

rows = result.getRows()
columns = result.getColumns()

// after all
